import React, {useState} from "react";
import Cell from "./Cell";
import './Board.css';

/** Game board of Lights out.
 *
 * Properties:
 *
 * - nrows: number of rows of board
 * - ncols: number of cols of board
 * - chanceLightStartsOn: float, chance any cell is lit at start of game
 *
 * State:
 *
 * - hasWon: boolean, true when board is all off
 * - board: array-of-arrays of true/false
 *
 *    For this board:
 *       .  .  .
 *       O  O  .     (where . is off, and O is on)
 *       .  .  .
 *
 *    This would be: [[f, f, f], [t, t, f], [f, f, f]]
 *
 *  This should render an HTML table of individual <Cell /> components.
 *
 *  This doesn't handle any clicks --- clicks are on individual cells
 *
 **/

function Board(props) {

  let boardProperties={
    nrows:4,
    ncols:9,
    chanceLightStartsOn:0.25
  };

  function constructor(props) {
    this.state = {
      board:createBoard(),
      hasWon: false
    };
    this.flipCellsAround = this.flipCellsAround.bind(this);
  }


  /** create a board nrows high/ncols wide, each cell randomly lit or unlit */

  function createBoard() {
    let board = [];
    // TODO: create array-of-arrays of true/false values
    for (let y = 0; y < this.props.nrows; y++) {
			let row = [];
			for (let x = 0; x < this.props.ncols; x++) {
				row.push(Math.random() < this.props.chanceLightStartsOn);
			}
			board.push(row);
		}
    return board
  }

  /** handle changing a cell: update board & determine if winner */

  function flipCellsAround(coord) {
    let {ncols, nrows} = this.props;
    let board = this.state.board;
    let [y, x] = coord.split(",").map(Number);


    function flipCell(y, x) {
      // if this coord is actually on board, flip it
      if (x >= 0 && x < ncols && y >= 0 && y < nrows) {
        board[y][x] = !board[y][x];
      }
    }

    // TODO: flip this cell and the cells around it
    flipCell(y, x);
    flipCell(y, x - 1); 
    flipCell(y, x + 1); 
    flipCell(y + 1, x); 
    flipCell(y - 1, x);
    // win when every cell is turned off
    // TODO: determine is the game has been won
    let hasWon = board.every((row) =>
      row.every((cell) => {
        return cell === false;
      })
    );
    this.setState(board);
    this.setState(hasWon);
  }

  /** Render game board or winning message. */

  function render() {
    const tableBoard = [];
    for (let y = 0; y < this.props.nrows; y++) {
      let row = [];
      for (let x = 0; x < this.props.ncols; x++) {
        let coord = `${y},${x}`;
        row.push(
          <Cell
            key={coord}
            coord={coord}
            isLit={this.state.board[y][x]}
            flipCellsAroundMe={this.flipCellsAround}
          />
        );
      }
      tableBoard.push(<tr key={`row${y}`}>{row}</tr>);
    }

    let gamePosition;
    this.state.hasWon
      ? (gamePosition = <h2>Winner!</h2>)
      : (gamePosition = (
          <div>
            <table className="Board-table">
              <tbody>{tableBoard}</tbody>
            </table>
          </div>
        ));

    return (
      <div className="Board">
        <h1>Lights Out Game</h1>
        {gamePosition}
      </div>

    // if the game is won, just show a winning msg along with the leaderboard

    // TODO

    // make table board
    // render leaderboard when won or lost

    // TODO
  );}
}


export default Board;
