import React from 'react';
import { useState, useEffect } from 'react';
import axios from 'axios';

function Leaderboard() {
    const [persons, setPersons] = useState([]);

    useEffect(() => {
        const url = new URL("http://localhost:8000/graphql/");
        url.searchParams.append("query", `query { allPersons { name, wins, loses } }`);
        axios.get(url)
            .then(({ data }) => setEntries(data.data.allPersons))
    }, [])


    return (
        // return a leaderboard using the html table tag
        // data should NOT be hardcoded, should be fetch using graphql from localhost:8000/graphql/
        // The styling is not a priority. You can refer to SecondStage.png but not necessary to be similar
        <React.Fragment>
            <h3>Your score</h3>
            <table class="table_score">
                <tr>
                    <th>Name</th>
                    <th>Wins</th>
                    <th>Loses</th>
                </tr>
                <tbody>
                    {entries.map((person, index) => (
                        <tr key={index}>
                            <td><strong>{person.name}</strong></td>
                            <td>{entry.wins}</td>
                            <td>{entry.loses}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </React.Fragment>
    );
}

export default Leaderboard;
