import React, { useState } from "react";
import Cell from "./Cell";
import './Board.css';
import Form from "./Form";

let triesleft = 20;

function Board() {

  let [board, setBoard] = useState(createBoard);
  let [hasWon, sethasWon] = useState(false);
  let [tries,setTries] = useState(triesleft)

  /** create a board nrows high/ncols wide, each cell randomly lit or unlit */

  function createBoard() {
    let freshboard = [];
    // TODO: create array-of-arrays of true/false values
    for (let y = 0; y < 4; y++) {
      let row = [];
      for (let x = 0; x < 9; x++) {
        row.push(Math.random() < 0.5);
      }
      board.push(row);
    }
    return freshboard;
  }
  /** handle changing a cell: update board & determine if winner */

  function flipCellsAround(coord) {
    let newboard = Object.assign([],board);
    let [y, x] = coord.split("-").map(Number);


    function flipCell(y, x) {
      // if this coord is actually on board, flip it
      if (x >= 0 && x < 9 && y >= 0 && y < 4) {
        newboard[y][x] = !newboard[y][x];
      }
    }

    // TODO: flip this cell and the cells around it
    flipCell(y, x);
    flipCell(y, x - 1);
    flipCell(y, x + 1);
    flipCell(y + 1, x);
    flipCell(y - 1, x);
    // win when every cell is turned off
    // TODO: determine is the game has been won
    let hasWon = board.every((row) =>
      row.every((cell) => {
        return cell === false;
      })
    );
    setBoard(newboard);
    sethasWon(hasWon);
  }

  /** Render game board or winning message. */


  const tableBoard = [];
  for (let y = 0; y < 4; y++) {
    let row = [];
    for (let x = 0; x < 9; x++) {
      let coord = `${y}-${x}`;
      row.push(
        <Cell
          key={coord}
          coord={coord}
          isLit={board[y][x]}
          flipCellsAroundMe={flipCellsAround}
        />
      );
    }
    tableBoard.push(<tr key={`row${y}`}>{row}</tr>);
  }

  let gamePosition;
  hasWon
    ? (gamePosition =
      <div>
        <h2 className='winningMessage'>Winner!</h2>
        <Form/>
      </div>
    )
    : (gamePosition = (
      <div>
        <table className="Board-table">
          <tbody>{tableBoard}</tbody>
        </table>
      </div>
    ));

  return (
    <div className="Board">
      <h1>Lights Out</h1>
      {gamePosition}
    </div>
  );
}
export default Board;
